vec3 h(vec3 c, float s){
    return c * mat3(c += .33 - (c = vec3(cos(s), s = -sin(s) * .6, -s)).x / 3., c.zxy, c.yzx);
}


vec3 h1(vec3 c, float s){
    vec3 u = vec3(.5774);
    return c * cos(s) + cross(u, c) * sin(s) + u * dot(u, c) * (1. - cos(s));
}


void mainImage(out vec4 o, in vec2 u){
	u-=iResolution.xy*.5;
    u/=iResolution.y;
	o=vec4(h(vec3(.9,.5,.2),atan(u.x,u.y))*step(u.x=length(u),.5)-step(u.x,.1),1.);
}

