// Homeomorfismo
float h(float x){ return pow(x, 4.); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalización
    vec2 uv = (fragCoord - .5 * iResolution.xy)/min(iResolution.x, iResolution.y);

    // Canales RGB
    vec3 textura0 = texture(iChannel0, uv).rgb;
    vec3 textura1 = texture(iChannel1, uv).rgb;
    // Máscara t.
    float mascara = min(texture(iChannel2, uv * 0.25).r * 2.0, 1.0);

    // Homotopías
    vec3 homotopia1 = mix(textura0, textura1, mascara);
    vec3 homotopia2 = mix(textura0, textura1, h(mascara));

    // Devolvemos la primera mitad, el canal normal
    // en la segunda mitad, la homotopía.
    if(abs(uv.x) < 0.01){
    	fragColor = vec4(1.0);
    }else if(uv.x <= 0.){
    	fragColor = vec4(homotopia1, 1.0);
    }else{
        fragColor = vec4(homotopia2, 1.0);
    }
}
