void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;

    float value1 = (uv.x + uv.y);
    float value2 = 1.0 - (uv.x + uv.y) * 0.5; // from bottom left 1 to top right 0

    float progress = fract(iTime * 0.25) ;
    float ramp = 0.5;

    progress = 1.0 - (progress + progress * ramp); // from 1 to -1

   	float sx = smoothstep( progress, progress + ramp, value2);
    // Output to screen
    fragColor = vec4(vec3(sx), 1.0);
}
