void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define strength iMouse.x / iResolution.x
#define darken iMouse.y / iResolution.y

float greenscreen(vec3 col) {
    float g = length(col - vec3(0.,1.,0.));
    return g;
}
float brightness(vec3 col) {
    return (col.r + col.g + col.b) / 3.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec3 col = texture(iChannel0, uv).rgb;

    float scale = 0.98;

    vec2 uv2 = uv - 0.5;
    uv2 = uv2 + 0.5 - (scale - 1.0) * 0.5;
    uv2 *= scale;

    vec3 buffer = texture(iChannel1, uv2).rgb;
  	col += min(buffer * greenscreen(buffer) * brightness(buffer), 1.0) * strength - darken;

    fragColor = vec4(col,1.);
}
