void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/iResolution.xy;
    vec3 conversion = vec3(255, 255, 255);

	vec3 webcam_pixel = texture(iChannel0, p).rgb;
	vec3 noise_pixel = texture(iChannel1, p).rgb;

    float gray_scale = dot(webcam_pixel, conversion);

    if(gray_scale > .5) fragColor = vec4(webcam_pixel, 1);
    else fragColor = vec4(noise_pixel, 1);
}
