//hacked together tton shader thing
const float color_r = 0.25;
const vec4 palette[4] = vec4[4](vec4(0,0,0,1),vec4(0,1,1,1),vec4(1,0,1,1),vec4(1,1,1,1));
const vec4 greenScreen = vec4(0.,1.,0.,1.);

#define threshold 0.55
#define padding 0.05

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 c = texture(iChannel0, uv);
    vec3 tc = vec3(1., 0., 0.);

    vec3 diff = c.xyz - greenScreen.xyz;
    float fac = smoothstep(threshold-padding,threshold+padding, dot(diff,diff));
    c = mix(c,vec4(0), 1.-fac);

    // Ramp colors
    float brightness = sqrt(
        0.299* (c.r*c.r) +
        0.587* (c.g*c.g) +
        0.114* (c.b*c.b) );

    vec4 t = palette[int(floor(brightness/color_r))];
    fragColor = mix(t,vec4(0.3), 1.-fac);;   
}
