   vec4 drawLine(vec2 pos, vec2 point1, vec2 point2, float width, vec3 color, float antialias) {
        float k = (point1.y - point2.y) / (point1.x - point2.x);
        float b = point1.y - k * point1.x;
        float d = abs(k * pos.x - pos.y + b) / sqrt( k*k + 1.);
        float t = smoothstep(width/2.0, width/2.0 + antialias, d);
        return vec4(color, 1.0 - t);
    }

 vec4 circle(vec2 pos, vec2 center, float radius, vec3 color, float antialias, float thickness) {
        float d = length(pos - center) - radius;
        float t = smoothstep(thickness, antialias + thickness, abs(d));
        return vec4(color, 1.0 - t);
    }


 vec4 circle2(vec2 pos, vec2 center, float radius, vec3 color, float antialias, float hl, float vl) {
        float k = vl / hl;
     	pos.y = pos.y * k;
     	float d = length(pos - center) - radius * k;
        float t = smoothstep(0., antialias, abs(d));
        return vec4(color, 1.0 - t);
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Output to screen
    vec2 coords = fragCoord.xy / iResolution.xy;
    fragColor = vec4(1.0, 1.0 , 1.0 ,1.0);
    vec2 point1 = vec2(0.0, 0.0)*iResolution.xy;
    vec2 point2 = vec2(1.0, 1.0)*iResolution.xy;
    vec2 point3 = vec2(0.5, 0.5)*iResolution.xy;
    vec4 layer1 = vec4(1.0, 1.0 ,1.0, 1.0);
    vec4 layer2 = drawLine(fragCoord, point1, point2, 3., vec3(1., 0., 0.), 2.);
    vec4 layer4 = circle(fragCoord, point3, 0.5 * iResolution.y,vec3(1.0, 0.0, 0.0), 3., 1.);
    vec4 color = mix(layer1, layer2, layer2.a);
    color = mix(color, layer4, layer4.a);
    fragColor = color;
}
