void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = fragCoord-0.5*iResolution.xy;

    float x= uv.x;
    float y= uv.y;

    float gray;

    if(((y+0.5)-f(x-0.5))*((y-0.5)-f(x+0.5))<=0.){
        gray = 1.;
    }else if(((y+0.5)-f(x+0.5))*((y-0.5)-f(x-0.5))<=0.){
    	gray = 1.;
    }else{
    	gray = 0.;
    }


    float r = gray;
    float g = gray;
    float b = gray;

    vec3 col = vec3(r,g,b);
    // Output to screen
    fragColor = vec4(col,1.0);
}
