void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 mouseNorm = iMouse.xy/iResolution.xy;
    float mouseX = clamp(mouseNorm.x, 0.5, 1.0);
    float mouseY = clamp(mouseNorm.y, 0.5, 1.0);
	vec2 uv = fragCoord/iResolution.xy*vec2(mouseX, mouseY);

    fragColor = uv.xyyy; // equivalent of vec4(uv.x,uv.y,uv.y,1.0);
}
