void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 col;
    float t;

    for(int c=0;c<3;c++){
	    vec2 uv = (fragCoord*50.0-iResolution.xy)/iResolution.y/20.0;
        uv += iTime/10.0;
        t = iTime+float(c)/10.;
        float scale = 5.0;
        float scale1 = 1.4;
        for(int i=0;i<20;i++)
        {

            uv=sin(uv-(uv.yx)/scale)*scale/2.0+scale1*scale;
            uv /= scale1;
        	//uv += cos(uv/100.0);
        	uv=uv+col.xy;
        }
     col[c] = fract((uv.x)/(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}

vec2 sound1(int t)
{
    //t = t|t/2/4096;
    //t = (t>>(t/(2+(t/4096)%3+(t/4096)%2)))|t>>4&t>>5|t>>6|t/2/4096;

    t = (t>>(t/(2+(t/4096)%3+(t/4096)%2)))|t/2/4096|t/3/4096;
    return vec2(float(t & 0xff - 128) / 128.);
}

vec2 mainSound( in int samp,float time){
    time *= 8000.0;
    float factor = .5;
    vec2 result = vec2(0.0);
    for(int i = 0; i < 4; i++){
    	result += sound1(int(time*factor))*factor;
        factor *= 1.5;
    }
    return result/100.0;
}
