#define CS(a) cos(a+vec2(0,11))
#define S(D)  smoothstep(9./R.y,0.,abs(length(D-U) - 1. ) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, I,
         U = 3.*( 2.*u - R ) / R.y;
    O-=O;

    mat2 M = mat2( 1,0,.5,.87);
    I = U *= M; U = mod(U,2.)-1.; U *= inverse(M);             // tiling
    float a = abs( fract( atan(U.y,U.x)/6.28*6. +.5 ) - .5);   // 6-rot + mirror 12-sym
    U = length(U) * CS( 6.28*a/6.);

    O += S( CS(-3.14/3.) );                                    // 1 arc â†’ rosette
  //O += S();                                                  // bounding circle
  //O += S( 1.73*CS(3.14/6.) );                                // bounding arc

    O += .5*texture(iChannel0,U/2.+.5);                        // BG texture
    O += .5*vec4(U,0,0);                                       // BG gradient
  //O += vec4(floor(I/2.)/4.,0,0);                             // BG tile id
}
