#define  S(a, b, t) smoothstep (a, b, t)

float heart(vec2 r, vec2 center, float radius)
{
    return 1.0 - smoothstep (radius, radius+0.002, length(r-center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 r = (fragCoord - iResolution.xy*.5)/iResolution.y;

    vec3 color = vec3(0, 0, 0); //  круга
    vec3 ret; // розрахунок
    vec2 uv = fragCoord/iResolution.xy;
    vec3 bg = 0.5 + 0.5*cos(uv.xyx+vec3(0,7,4)); // колір фону
    float yu = iTime;
    yu = mod(yu,2.0)-1.0;
    float heartColor = heart(r,vec2(yu,-yu),0.3);
    ret = mix(bg,color, heartColor );

    float d = length(r);
    vec3 col = vec3(1);
    vec3 pixel = ret;
 float c = S(.0, .0, d);
 col = vec3(c *2., .15, 0.3);

     fragColor = vec4(pixel,3.0);

}
