void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//    fragColor = texture(iChannel0, (floor( (fragCoord*.98 + iResolution.xy*.01 + (fragCoord-iResolution.xy/2.).yx*vec2(-.03,.03)) )+.5) / iResolution.xy);
    fragColor = texture(iChannel0, (fragCoord*.98 + iResolution.xy*7.01 + (fragCoord-iResolution.xy/2.).yx*vec2(-.03,.03)) / iResolution.xy);

    float t = iTime*19.90;

    vec4 col = vec4(sin(-t*-vec3(3,11,171))*.5+.5,1);
    float idx = .0+1.0*smoothstep( -6., 280., length( fragCoord - sin(vec2(11,13)*t)*-6. - iResolution.xy/5. ) );
    fragColor = mix( -col *-col, fragColor, idx * idx );
}
