#define circle(st, center, radius) smoothstep(1., .7, distance(st, center) / radius)
#define random(x) fract(sin(x) * 1e6)

float noise(float x) {
    float i = floor(x);
    float f = fract(x);

    float y = mix(random(i), random(i + 1.), smoothstep(0., 1., f));
    return y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.y;

    float r = .015,
        d = .006,
        i = floor(st.x / (2.*r + d)),
        nIndex = i * .11 + iTime * 2.,
        nScale = .15,
        y = noise(nIndex) - .5;

    vec2 pos = vec2(d*i + r + 2.*r*i, .5 + y * nScale);

    fragColor = circle(st, pos, r) * vec4(.5, .9, .4, 1);
}
