void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

        uv = (1.0 - 2.0 * uv) * vec2(iResolution.x / iResolution.y, 1);

    vec2 uv_n = uv*2.2;
    float len;
    const int mult = 13;
    for(int i = 0; i < mult; i++){
        len = length(uv_n);
        uv_n.x += sin(uv_n.y - cos(len)) + cos(iTime/9.0);
        uv_n.y += cos(uv_n.x - sin(len)) + sin(iTime/12.0);
    }

    uv = fract(uv*2.0)-0.5;
    uv = mix(uv, uv*sin(len), 0.4);
    float shape = step(0.25, length(uv));

    vec3 col = vec3(shape);

    fragColor = vec4(col,1.0);
}
