#define time iTime

mat2 r(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  ( fragCoord -.5* iResolution.xy ) / iResolution.y;
    vec2 uvv = uv;
     uv *=1. + cos(-time/10.)*2.;
     uv *=r(sin(10.*length(uvv)+time)*2.);
    uv = abs(fract(uv)-vec2(.5));
    uv *= r(length(uvv)+time/2.);

    float d = fract(length(uv.y+(uv.x))+time);

    uv *= r(length(uvv)-time/2.);

    vec3 col = smoothstep(0.5,0.01,mix(vec3(0.),vec3(.9,.5,.1),d));
    col.r += 1.-length(uvv);
    col.b -= pow(1.-length(uvv),5.);
    col *= length(col)*(1.-length(uvv));
    fragColor = vec4(
        col,
        1.0);
}
