#define STEPS 200
float mand(vec2 z, vec2 c) {
    int iter = 0;
    float sqrDist = 0.0;
    for (;iter<STEPS;iter++) {
        float rr = z.x*z.x;
        float ii = z.y*z.y;
        float tmpR = rr - ii + c.x;
        float tmpI = z.x * z.y + z.y * z.x + c.y;
        sqrDist = length(z);
        if (sqrDist > 4.0) {
            break;
        }
        z.x = tmpR;
        z.y = tmpI;
    }
	return float(iter) - (log (log (sqrDist))) / log(2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x - 0.2;
	vec2 mandelCoords = uv.xy*(1.0/(iTime*iTime))-0.57;

    float mu = mand(mandelCoords, mandelCoords);
    vec3 col = vec3( 1.0 - mu / float(STEPS));
    fragColor = vec4(col,1.0);
}
