#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U))
void mainImage( out vec4 Q, in vec2 U )
{
    vec2 u = .5+.1*vec2(sin(.1*iTime),cos(.1*iTime));
    if (iMouse.z>0.) u = iMouse.xy/R;
    float r = length(U-u*R)/R.y;

    vec4 a = A(U);
    Q = 0.5+0.5*sin(.1*iTime+r+atan(a.y,a.x)*vec4(1,2,-1,4));
	Q*=Q;
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
void mainImage( out vec4 Q, in vec2 U )
{
   	vec2 u = .5+.1*vec2(sin(.1*iTime),cos(.1*iTime));
    if (iMouse.z>0.) u = iMouse.xy/R;
    U -= u*R; 
    float r = length(U)/R.y,
         a = .05*sin(.3*iTime)/(1.+15.*r);

    U *= (1.-.01*r)*mat2(cos(a),-sin(a),sin(a),cos(a));
    U += u*R;

    vec4
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));

    Q.z = -.25*(n.y - s.y + e.x - w.x);
    Q.x = .25*(e.z - w.z);
    Q.y = .25*(n.z - s.z);
    if (length(Q.xy)>0.) Q.xy = mix(Q.xy,.001*normalize(Q.xy),.99);
    else Q = sin(U.x+U.y*vec4(1,2,3,4));
    Q = mix(Q,0.25*(n+e+s+w),1.-1e-4-.1*r);


}

