const float PI = 3.141592;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord.xy - iResolution.xy * 0.5;

    float phi = atan(coord.y, coord.x + 1e-6);
    phi = phi / PI * 0.5 + 0.5;
    float seg = floor(phi * 6.0);

    float theta = (seg + 0.5) / 6.0 * PI * 2.0;
    vec2 dir = vec2(cos(theta), sin(theta));
    float l = dot(dir, coord);

    float phase = iTime * 0.8;
    float w1 = sin(floor(phase    ) + seg) * 40.0 + 60.0;
    float w2 = sin(floor(phase + 1.0) + seg) * 40.0 + 60.0;
    float w = mix(w1, w2, smoothstep(0.75, 1.0, fract(phase)));

    float prog = l / w + iTime * 2.0;
    float thresh = fract(73.8493748 * abs(sin(floor(prog) * 4.67458347)));
    float c = clamp((fract(prog) - thresh) * w * 0.3, 0.0, 1.0);

    fragColor = vec4(c, c, c, 1);
}
