Main {
    U -= 0.5*R;
    U *= .5;
    U += 0.5*R;
    float
        n = length(B(U+vec2(0,1)))+length(D(U+vec2(0,1))),
        e = length(B(U+vec2(1,0)))+length(D(U+vec2(1,0))),
        s = length(B(U-vec2(0,1)))+length(D(U-vec2(0,1))),
        w = length(B(U-vec2(1,0)))+length(D(U-vec2(1,0)));
	vec4 a = A(U), b = B(U), c = C(U), d = D(U);  
    vec3 no = normalize(vec3(e-w,n-s,1));
    Q = atan(5.*vec4(length(b),0,length(d),1));
	Q *= 0.5+0.5*no.x;
}

Main {
	Q = A(U);
    Neighborhood2;

    vec4 dy = (u-d);
    vec4 dx = (r-l);

    Q.x += dt*(dx.z-dy.w);
    Q.y += dt*(dx.w+dy.z);
    Q.z += dt*(dx.x+dy.y);
    Q.w += dt*(dx.y-dy.x);

    float m = 0.;
    for (int x = -3; x<=3; x++)
    for (int y = -3; y<=3; y++)
    {
        vec4 q = C(U+vec2(x,y));
        float l = length(vec2(x,y));
    	m += dot(q,q)/(1.+l);
    }
    float p = -K0/(2.+length(U-0.5*R));
    float p1 = K1*m;
    vec4 q = C(U);
    Q.xy *= e(-dt*(1.+p+p1+K2*(dot(q.zw,q.zw)-dot(q.xy,q.xy))));
    Q.zw *= e(-dt*(1.+p+p1-K2*(dot(q.zw,q.zw)-dot(q.xy,q.xy))));


    if (iFrame < 1) {Q = exp(-.01*dot(U-0.5*R,U-0.5*R))*vec4(1);}
    if (length(U-0.5*R)>0.3*R.y) Q *= 1.-1./R.x;
    if (iMouse.z>0.&&length(U-iMouse.xy)<13.)
        Q.xz = vec2(-.1);

}

