#define PI 3.14159265
#define TAU (2.0 * PI)
#define S(a,b,n) smoothstep(a, b, n)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	float aspect =  iResolution.x / iResolution.y;
    float progress = iMouse.x / iResolution.x;
    float swirlRadius = 0.5;

    // Time varying pixel color
    vec2 center = uv - 0.5;

    float dist = length(center * vec2(aspect, 1.0));
    float angle = atan(center.y, center.x) + TAU * progress * S( swirlRadius, 0.0, dist );
    float radius = length(center);

    vec2 coord = vec2(cos(angle), sin(angle)) * radius + 0.5;

    vec4 color = texture(iChannel0, coord);

    // Output to screen
    fragColor = color;
}
