void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uva = uv;
	/*uv.y*=(sin(iTime/10.0)+1.0)/2.0;
    uv.x*=(cos(iTime/10.0)+1.0)/3.0;
    */

    float freq = 5.0 + sin(iTime)*3.0;

    float split =15.0;
    float dsp = (1.0/split)*2.0;
    float esp = (1.0/split);
    if(mod(uv.x,dsp)>esp){
        uv.x+=esp;
    }
    else{
        uv.x-=esp;
    }

    if(mod(uv.y,dsp)>esp){
        uv.y+=esp;
    }
    else{
        uv.y-=esp;
    }


    uv.x+=sin((uv.y*freq)+iTime)/5.0;


    uv.x=mod(uv.x,1.0);
    uv.y=mod(uv.y+(iTime/split), 1.0);

    // Time varying pixel color
    vec4 col = texture(iChannel0,uv);

    col.r+=floor(uva.x*split)/split;
    col.b+=mod(1.0 - uva.y, 1.0);

    // Output to screen
    fragColor = col;
}
