bool check(float index){
    float res = sqrt(index);
    return res == floor(res);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;
    float res = 10.0;
    vec2 u = fract(uv * res);
    float l = res * 2.0 / iResolution.x;
    float grid = max(step(u.x,l), step(u.y,l));
    vec2 i = ceil(uv * res);
    float count = i.x + i.y * i.x;
    float k = check(count) ? 1.0 : grid;
    fragColor = vec4(k);
}
