// The step function can be used to draw thick lines 
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Center and normalize the reference system
    vec2 U = ( 2. * fragCoord - iResolution.xy ) / iResolution.xy.y;

    // Thickness 
    float line_thickness = .1;

    // The center of the line is the vertical line in the middle of the screen
    float line_position = 0.;

    // Step() return 1 if the distance of fragCoord.x from the line position
    //  is less than the thickness, otherwise 0
    float c = step( abs(U.x - line_position), line_thickness);
    fragColor = vec4(c, c, c, 0.0);
}
