float s(vec2 p,vec2 a,vec2 b,float c )
{
	p*=mat2(a.x,a.y,-a.y,a.x);
	p.x=abs(p.x);
	return sqrt(dot(p,p)+0.25-((b.y*p.x>b.x*p.y)?dot(p.xy,b):length(p.xy)))-c;
}

void mainImage(out vec4 o,in vec2 c)
{
	vec2 u=(c*2.0-iResolution.xy)/iResolution.y; 
	u.y+=1.0-0.08*sin(u.x*25.0)-0.05*mod(iTime,37.0);
	u.x+=1.5-0.1*mod(iTime*2.0,37.0);
	o=vec4(step(1.0,1.0-s(u,vec2(sin(1.67),cos(1.67)),vec2(sin(0.01),cos(0.01)),0.01)),
		step(1.0,1.0-s(u,vec2(sin(1.1),cos(1.1)),vec2(sin(0.5),cos(0.5)),0.02)),0,1.0);
}
