void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uv_center = uv - 0.5;
    uv_center.y *= iResolution.y / iResolution.x;

    vec2 uv_center_abs = abs(uv_center);
    float dist_max = max(uv_center_abs.x, uv_center_abs.y);

    float size = 0.2;
    float highlight = 0.001;
    float grad = 0.02;


    float inner = smoothstep(size - highlight - grad, size - highlight, dist_max);
    float outer = smoothstep(size + highlight + grad, size + highlight, dist_max);


    float val = inner * outer;

    vec3 col0 = vec3(1, 0, 0);
    vec3 col1 = vec3(0, 1, 0);
    vec3 colMix = mix(col0, col1, sin(uv.y * 5. + iTime * .5) * 0.5 + 0.5);

    vec3 col = mix(colMix, vec3(1.), val);

    float blink = 1. - step(sin(iTime * 2.), -0.9) * (sin(iTime * 50.) * 0.5 + 0.5);


    col = col * val * blink;

    // Output to screen
    fragColor = vec4(col,1.0);
}
