#define PI 3.14159265359
#define SQRT2 1.41421356237

#define MAX_FLT 3.402823e+38
#define EPSILON 1.192093e-07

#define sqrLength(x) dot((x),(x))

float clamp01(in float v) { return clamp(v, 0.0, 1.0); }
vec2 clamp01(in vec2 v) { return vec2(clamp01(v.x), clamp01(v.y)); }

//////////////
// Controls //
//////////////

// 1 for polar, 0 for cartesian 
#define IS_USING_POLAR_COORD 1

// 1 for applying transformation to whole image, 0 for lerping using alpha
#define IS_WHOLE_IMAGE 1

// Shows the mix ratio between transformed uv (white) and non-transformed uv (black)
#define IS_SHOWING_TRANSFORMED_UV_MIX 0

//////////////////////
// Texture Sampling //
//////////////////////

vec4 sample_clamp(in sampler2D tex, in vec2 uv) 
{
    return textureGrad(tex, clamp01(uv), dFdx(uv), dFdy(uv));
}

vec4 sample_repeat(in sampler2D tex, in vec2 uv) 
{
    return textureGrad(tex, fract(uv), dFdx(uv), dFdy(uv));
}

vec4 sample_border(in sampler2D tex, in vec2 uv) 
{
    bool isInBorder = ((0.0 <= uv.x) && (uv.x <= 1.0) && (0.0 <= uv.y) && (uv.y <= 1.0));
    if (isInBorder)
        return textureGrad(tex, fract(uv), dFdx(uv), dFdy(uv));
    return vec4(1.0, 0.0, 1.0, 1.0); // bg color - magenta    
}

////////////////////////
// Coordinate Systems //
////////////////////////

vec2 tex_to_cartesian(in vec2 uv, in vec2 origin)
{
    uv = 2.0 * uv - 1.0;
    uv -= origin;
    return uv;
}

vec2 cartesian_to_tex(in vec2 uv, in vec2 origin)
{
    uv += origin;
    uv = 0.5 * uv + 0.5;
    return uv;
}

vec2 tex_to_polar(in vec2 uv, in vec2 origin)
{
    uv = tex_to_cartesian(uv, origin);
    float r = length(uv); 
    float theta = atan(uv.y, uv.x);
    return vec2(r, theta);
}

vec2 polar_to_tex(in vec2 rt, in vec2 origin)
{
    float x = rt.x * cos(rt.y);
    float y = rt.x * sin(rt.y);  
    return cartesian_to_tex(vec2(x,y), origin);
}

////////////////
// Transforms //
////////////////

mat2 rotation_matrix(in float alpha)
{
    float cosa = cos(alpha);
    float sina = sin(alpha);
    return mat2(cosa, -sina, sina, cosa); 
}

vec2 get_origin(in float t)
{
	return 0.8 * vec2(cos(t), sin(t));
}

vec2 transform(in vec2 uv, in float t)
{
    float bias_r = 0.0; // pinch / explode
    float bias_t = 0.0; // rotation    
    float scale_r = 1.0; // zoom
    float scale_t = 1.0; // starburst (fins) / rings
    float shear_r = 0.0; // ?
    float shear_t = 0.0; // spiral
    return mat2(scale_r, shear_t, shear_r, scale_t) * uv + vec2(bias_r, bias_t);    
}

////////////
// Driver //
////////////

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    vec2 origin = get_origin(iTime);
    vec2 uv_o = fragCoord / iResolution.xy;

    // uv_o -> uv_s : tex coord -> cartesian / polar coord   
#if IS_USING_POLAR_COORD
    vec2 uv_s = tex_to_polar(uv_o, origin);
#else
    vec2 uv_s = tex_to_cartesian(uv_o, origin);
#endif

    // uv_s -> uv_t : transform   
    vec2 uv_t = transform(uv_s, iTime);

    // uv_t -> uv_e : transformed cartesian / polar coord -> transformed tex coord
#if IS_USING_POLAR_COORD
	vec2 uv_e = polar_to_tex(uv_t, origin);
#else
    vec2 uv_e = cartesian_to_tex(uv_t, origin);
#endif    

    // uv : mixing (using alpha) between original tex coords (uv_o) and transformed tex coords (uv_e)
    float alpha = 1.0;
#if !IS_WHOLE_IMAGE
    alpha = 1.0 - pow(smoothstep(0.0, 1.0, 3.0 * distance(uv_e, 0.5 * origin + 0.5)), 2.0);
#endif
    vec2 uv = mix(uv_o, uv_e, alpha);


    vec3 col = sample_repeat(iChannel0, uv).rgb;
#if IS_SHOWING_TRANSFORMED_UV_MIX
    col = vec3(alpha);
#endif

    fragColor = vec4(col,1.0);
}
