const float d_limit = 1.0;//지름 제한 diameter limit, you can change it
const float size = 10.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = floor(fragCoord/size)*size/iResolution.xy;
    vec2 q2 = fract(fragCoord/size);

    float value = texture(iChannel0,q).r;
    fragColor = vec4(step(length(q2-0.5),min(value,d_limit)))*value;
    //fragColor = step(length(q2-0.5),min(value,d_limit))*texture(iChannel0,q);
    //fragColor = smoothstep(d_limit+0.1,min(value,d_limit),length(q2-0.5))*texture(iChannel0,q);
}
