vec3 hsl2rgb( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
   vec2 uv = fragCoord/iResolution.xy;


    float pixel_x = uv.x /9. - 1.17;
    float pixel_y = uv.y /15. + 0.6;

    float X = pixel_x;
    float Y = pixel_y;
    float n = 120.;

	for (float k=17.;k>0.;k-=0.2) {

		float X_new = X*X - Y*Y + 0.0-cos(iTime/5.)/55.;
		float Y_new = 2.*X*Y    + 0.705-sin(iTime/5.)/55.;
        X = X_new;
        n=n-1.;
        Y = Y_new;

		if (tan(X_new+sin(Y_new-iTime*0.3))<=9.) {

        } else {

          fragColor = vec4( hsl2rgb( 
                           vec3(.2+k/20. -iTime*0.1, 0.8, 0.5+ cos(n/5.+iTime)*n/180.)
                      ),1.0);
           break;
        }



	}


}
