float f(float x)
{
    return exp(-x*x/2.0);
}

void
mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float u = (fragCoord.x/iResolution.x) * 2.0;
    float v = (fragCoord.y/iResolution.y - 0.5) * 2.0;

    if (0.0 <= u && u <= sqrt(f(v/u)))
        fragColor = vec4(0.75, 0.25, 0.25, 1.0);
    else
        fragColor = vec4(0.25, 0.75, 0.25, 1.0);
}
