void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = fragCoord/iResolution.xy;
  vec2 q = uv - vec2(0.5, 0.5);
  q.xy = q.yx;
  float p1 = 0.5;
  float te1 = iTime/10.;
  float te3 = te1/20.;
  float te5 = te3/40.;
  q.x = sin(te1) * q.x;
  float R = sin(te1);
  float G = cos(te1);
  float B = sin(q.x)/cos(q.y);
  vec3 col = p1 * vec3(R, G, B);
  float fC = sin(te1);
  float f1 = fC * sin(q.x/q.y);
  float pulse = sin(
    (cos(te1) * cos(te5))
    /(f1 * sin(te3) * cos(te3)));
  vec2 tig = vec2(sin(te1), cos(te3));
  //opt1:
  tig *= tig;
  vec2 squig = vec2(sin(pulse), cos(pulse));
  //opt2:
  squig *= squig;
  col *= 0.2 / length(q * tig * squig);
  // Mix in sound
  vec2 sound = texture( iChannel1, vec2(0.1,0.3) ).xy;
  col.x *= sound.x;
  col.y *= sound.y;
  // Output to screen
  fragColor = vec4(col,1.0);
}
