    //    F += -0.5*dm*exp(-d*d)*P.M.x;
    //   // P.M.y += 0.1*exp(-40.*d*d);
    //}

    //get gradient of "potential" at position (sort of)
    vec2 PsX = (P.X+P.V*dt);
    PsX = pos;
    //vec2 Ef = vec2(
    //    (texel(ch_wave, PsX+vec2(1,0)).r-texel(ch_wave, PsX+vec2(-1, 0)).r)/2.0,
    //    (texel(ch_wave, PsX+vec2(0,1)).r-texel(ch_wave, PsX+vec2( 0,-1)).r)/2.0
    //);
    vec2 Ef = texel(ch_wave,PsX).xy;
    //Ef.x = 1.;
    F -= Ef*P.M.y*0.025*P.M.x;

    //integrate
    //P.V += clamp((clamp((pos-R*0.5),-b,b)+R*0.5-pos),-1.0,1.0);
    P.V += F*dt/P.M.x-P.V*0.0;
    //border 
    //vec3 N = bN(P.X);
    //float vdotN = step(N.z, border_h)*dot(-N.xy, P.V);
