    float v = length(P.V);
    float v_limit = 0.5;
    P.V /= (v > v_limit)?(v/v_limit):1.0; //dont want mach cones.. (FTL)
}

void mainImage( out vec4 U, in vec2 pos )
{    
    R = iResolution.xy; time = iTime; Mouse = iMouse;
    ivec2 p = ivec2(pos);

    //define absorbing boundary to minimize echoes using rounded rect from sdf?
    //vec4 bg = vec4(1,1,1,1);
    //float ramp_width = 4.0;
    //vec2 b = R*0.5-vec2(ramp_width,ramp_width);
    //vec2 q = abs((pos-R*0.5)) - b;
    //float sd =length(max(q,0.0)) + min(max(q.x,q.y),0.0);
    //bg.b=clamp(1.0-sd/ramp_width,0.0,1.0);
    //it gets attracted to the wall if absorbing!

    vec4 col = vec4(0.0,0.0,0.0,0.0);
    vec4 wave_data = texel(ch1, pos);
    vec2 v = wave_data.xy-wave_data.ba;
    vec4 L = -4.0*wave_data;
    L += texel(ch1, pos+vec2( 1, 0));
    L += texel(ch1, pos+vec2(-1, 0));
    L += texel(ch1, pos+vec2( 0, 1));
    L += texel(ch1, pos+vec2( 0,-1)); 
    col.xy = wave_data.xy+v*0.995+0.4*L.xy; //max wave speed is 0.5 cell per step (0.5^2 in wave eq)
    col.b = wave_data.r;//store prev state
    col.a = wave_data.g;//store prev state

    if(false){
        //test source mode
        vec2 m_p = R*0.25;
        if(int(pos.x)+1 == int(m_p.x) 
        && int(pos.y) == int(m_p.y)){
            col.x = 1.0;
            //col.y = 0.0;
        }
        if(int(pos.x)-1 == int(m_p.x) 
        && int(pos.y) == int(m_p.y)){
            col.x = -1.0;
            //col.y = 0.0;
        }
        if(int(pos.x) == int(m_p.x) 
        && int(pos.y)+1 == int(m_p.y)){
            //col.x = 0.0;
            col.y = 1.0;
        }
        if(int(pos.x) == int(m_p.x) 
        && int(pos.y)-1 == int(m_p.y)){
            //col.x = 0.0;
            col.y = -1.0;
        }
    }else{
            vec4 accum = vec4(0,0,0,0);
            float w_tot = 0.0;
            range(i, -1, 1) range(j, -1, 1)
            {
                vec2 tpos = pos + vec2(i,j);
                vec4 data = texel(ch0, tpos);
                particle P0 = getParticle(data, tpos);
                float r = length(vec2(i,j));
                float w = exp(-(r*r));
                accum.xy += P0.M.x*P0.M.y*w*vec2(i,j);
                w_tot += w;
            }
            vec2 constraint = accum.xy/w_tot;
            col.xy += constraint;     
    }
    //if(bg.b<1.0){
    //    col.r = 0.0;
    //}

    U = col;
    //U = vec4(pos.x/1000.0,0.0,0.0,0.0);
}

