void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/vec2(iResolution.y,iResolution.y)*4.0;
    st.x += iTime/2.0+sin(st.y*3.0);
    st.y += iTime/2.0+sin(st.x*.25);
    vec3 color;
    color=floor(vec3(mod(st.x,1.0) + mod(st.y,1.0))/(sin(iTime)/2.0+1.0));
	fragColor = vec4( color, 1.0 );
}
