renderpass.code:
float circle(vec2 p) {
	return length(p)+0.5;
}

float frame(vec2 p, float h) {
	return step(p.y, 1.0-h);
}

// Noise (from iq)
float noise (in vec2 p) {
    float f = 0.0;
    p+=iTime*0.05;
    f += texture(iChannel0, p*0.025).r*0.05; f*=3.0;
    f += texture(iChannel0, p*8.02).r; f*=1.5;
    f += texture(iChannel0, p*16.04).r; f*=0.75;
    f += texture(iChannel0, p*32.03).r; f*=0.325;
	return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.0*fragCoord/iResolution.xy-1.0;
	uv.x*=iResolution.x/iResolution.y;

    vec2 p = uv;

    p.y -= 0.35;
    float top = frame(p+noise(uv*1.25)*1.2, 0.15);

    p.y += 0.45;
    float bottom = 1.0-frame(p+noise(uv*0.55)*0.86, 0.99);

    uv.x += 0.3+sin(iTime)*.3;
    float blu = step(0.95, circle(-0.5+uv+noise(uv)));

    uv.x += -1.2+sin(iTime)*.3;
    uv.y-=0.5;
    float giallo = step(0.95, circle(uv+noise(uv)));

    vec3 bg = vec3(0.99);
    vec3 green = vec3(0.0, 0.75, 0.);

    vec3 circle_giallo = vec3(0.95, 0.95, 0.0) * 1.0 - giallo;
    vec3 circle_blu = vec3(0.1, 0.1, 0.8) * 1.0 - blu;

    // circles
	vec3 col = mix(circle_blu, circle_giallo, 1.0-giallo);
    col = mix(col, green, 1.0-blu-giallo);

    // bg
    col = mix(bg, col, 1.0-(blu*giallo));

    vec3 head = vec3(0.6-top, 0.4-top, 0.0);
    vec3 foot = vec3(bottom);

    // bars
    col = mix(col, foot, 1.0-bottom);
    col = mix(col, head, 1.0-top);

    // grain
    col *= 0.85+(texture(iChannel0, uv*2.0).r*0.15);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
