vec4 sunRing(vec2 uv)
{
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;
    float d = length(uv * .8);

    // Time varying pixel color
    vec3 col = vec3(d);
    col = vec3(col.x * .5,col.y * .5,0.) / 2.;

    return vec4(col.r,col.g,col.b,1);



}
vec4 Sun(vec2 uv)
{
    uv -=.5;
    uv.x *= (iResolution.x / iResolution.y);

    float i = length(uv *1.5);
    vec3 color = vec3(-i + 1.);

    vec4 col2 = vec4(0.8 + color.x,0.4 + color.y,0.5 + color.z,1.)* .9;

    return col2;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = sunRing(uv);
    vec4 col2 = Sun(uv);






    // Output to screen
    fragColor = vec4(col2 + col);
}
