#define PIXEL_SIZE 2.

bool flipping = true;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float frame = mod(floor(iTime*3.), 6.0);

    uv = vec2((frame + uv.x) / 6.45, uv.y);
    vec2 metapixel = floor(fragCoord / PIXEL_SIZE);
    vec2 pixel_center = metapixel * PIXEL_SIZE + PIXEL_SIZE / 2.;
    vec4 real_texel = texture(iChannel0,uv);
    vec3 col = real_texel.xyz;
    float circle_radius = PIXEL_SIZE / 2. - 0.25;
    float led = 1.;


    bool even_row = mod(metapixel.y, 2.) == 0.;
    bool even_clm = mod(metapixel.x, 2.) == 0.;
    if (flipping && (iFrame % 2 != 0)) {
        even_row = !even_row;
    }
    if (flipping && (iFrame % 2 == 0)) {
        even_clm = !even_clm;
    }
    if (even_row) {
        if (even_clm) { col = vec3(led * real_texel.r,0.,0.); } 
         else { col = vec3(0.,0., led * real_texel.b); }
    } else {
        if (even_clm) { col = vec3(0., led * real_texel.g,0.); }
         else {
            float MAX = max(max(real_texel.r, real_texel.g), real_texel.b);
            if (MAX > 0.5) { MAX = (MAX-0.5)*2.0; } else { MAX = 0.; }
            col = led * vec3(MAX); }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
