const float pi = 355. / 113.;
float psin (float t) {
    return (1. + sin (pi * t - pi/2.)) / 2.;
}
float pcos (float t) {
    return (1. + cos (pi * t)) / 2.;
}

vec3 reducePallette (vec3 c, float r) {
    if (0. == r) {
        return c;
    }

    return mod (c, 1. / r) * r;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec4 s = texture(iChannel0, uv);
    float k = floor (32. * pcos (.08*iTime));
    fragColor = vec4 (reducePallette (s.rgb, k), s.a);
}
