void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    uv *= 20.;

    float x = 0.;
    for (int i = 0; i < 10; ++i) {
        float t = float(i) * iTime / 20.;
        x += cos(cos(t) * radians(360.) * uv.y + sin(t) * radians(360.) * uv.x);
    }

    vec3 col = vec3(1.) * (.5 + .5 * x);
    fragColor = vec4(col, 1.);
}
