void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

float snoise(in float x) {
    return mix(Hash11(floor(x)), Hash11(ceil(x)), smoothstep(0.0, 1.0, fract(x)));
}

float fbm(in float x, in float scale, in int octaves) {
    x *= scale;

    float value = 0.0;
    float nscale = 1.0;
    float tscale = 0.0;

    for (int o=0; o < octaves; o++) {
        value += snoise(x) * nscale;
        tscale += nscale;
        nscale *= 0.5;
        x *= 2.0;
    }

    return value / tscale;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;

    vec2 motion = sin(fbm(iTime, 1.0, 5) * 6.28 + vec2(1.57, 0.0)) * 0.025;
    fragColor = texture(iChannel0, fract(((uv + motion) * iResolution.y + center) / iResolution.xy));

    float c = exp((0.01 - length(uv)) * 25.0);
    fragColor.rgb = mix(fragColor.rgb, vec2(0.0, 1.5).rgg, c);

    fragColor *= 0.98;
}
