void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Scenes
    vec4 scene1 = texture(iChannel0, uv); 
    vec4 scene2 = texture(iChannel1, uv); 

    // Shape
    float strength = min(2.0 * fract(iTime * 0.05), 1.0);
    float panel = step(strength, uv.x);
    float clock = ceil(mod(iTime * 0.2, 2.0)) - 1.0;

    // Mask
    float mask = (1.0 * clock) + (1.0 - 2.0 * clock) * panel;

    // Output to screen
    fragColor = scene1 * vec4(mask) + scene2 * vec4(1.0 - mask);
}
