float ln (vec3 p, vec3 a, vec3 b) {return length(p-a-(b-a)*min(dot(p-a,b-a),0.)/dot(b-a,b-a));}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = (C(U).xxxx);
    Q = .6*abs(Q.x)+.8*vec4(Q.x,0,-Q.x,1);
}

// Calculate forces and pressure
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 
        d = D(U),
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    Q.xy -= 
        // electric force
        -d.zw*(.9-abs(d.x))*d.x+
        // pressure force
        0.25*vec2(e.z-w.z,n.z-s.z)+
        // magnus force
        0.25*Q.w*vec2(n.w-s.w,e.w-w.w);
    // divergence
    Q.z  = 0.25*(s.y-n.y+w.x-e.x+n.z+e.z+s.z+w.z);
    // curl
    Q.w = 0.25*(s.x-n.x+w.y-e.y);

    //Boundary conditions
    if ((iMouse.z>0.)&&length(iMouse.xy-iMouse.zw)>0.) Q.xy = mix(Q.xy,.7*normalize(iMouse.xy-iMouse.zw),0.3*smoothstep(1.,-1.,length(U-iMouse.xy)-6.));
    if (iFrame < 10 && length(U-0.5*R)<30.) Q.x = .5;
    if (iFrame<1) Q = vec4(0);
    }

// Advect along velocity and curl feild
void mainImage( out vec4 Q, in vec2 U )
{
    for (float i = 0.; i< N;i++) {
        Q = A(U);
        float co = cos(Q.w/N), si = sin(Q.w/N);
        U -= Q.xy*mat2(co,-si,si,co)/N;
    }
    Q = A(U);
}

// Advect along velocity and curl feild
void mainImage( out vec4 Q, in vec2 U )
{
    for (float i = 0.; i< N;i++) {
        Q = A(U);
        float co = cos(Q.w/N), si = sin(Q.w/N);
        U -= Q.xy*mat2(co,-si,si,co)/N;
    }
    Q = C(U);
}

// draw color and acount for divergence
void mainImage( out vec4 Q, in vec2 U )
{
    Q = C(U);
    // neighborhood
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0)),
    	nc = C(U+vec2(0,1)),
        ec = C(U+vec2(1,0)),
        sc = C(U-vec2(0,1)),
        wc = C(U-vec2(1,0)),
        m = 0.25*(nc+ec+sc+wc);
    // divergence 
    Q += 0.25*(s.y*sc-n.y*nc+w.x*wc-e.x*ec);
    // sim
    Q.x += 0.005*(1.-abs(Q.y))*Q.y;
    Q.x = mix(Q.x,m.x,.1);
    Q.y = mix(Q.x,m.y,.9);
    Q.zw = vec2(ec.y-wc.y,nc.y-sc.y);
    // boundary conditions
    if (iFrame<1) Q = vec4(cos(.1*U.x),0,0,0);
    }
