void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;
    if (xy.y > .5) {
    	xy.y = 1. - xy.y;
    }
    if (xy.x > .5) {
    	xy.x = 1. - xy.x;
    }
    vec4 camera = texture(iChannel0, xy);
    camera /= vec4(xy,1,0);
    fragColor = camera;
}
