const vec2 juliaC = vec2(0.3, -0.5);
const float juliaScale = 0.01;
const float juliaZoom = 0.5;
const int maxIterations = 255;

vec2 complexZ(const in vec2 z)
{
	return vec2
    (
 		z.x * z.x - z.y * z.y,
 		z.x * z.y * 2.0
	);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy - iResolution.xy * 0.5;
	uv *= 2.0 / min( iResolution.x, iResolution.y );

    vec2 z = uv * juliaZoom;
    float scale = juliaScale;

    vec3 fractalCol = vec3(0.0);
    int count = 0;
	for (; count < maxIterations; ++count) 
    {
 		z = complexZ(z) + juliaC;
 		if (dot(z, z) > 2.0)
        {
            break;
 		}

        fractalCol = texture(iChannel0, uv * 1.25).rgb;

        float d = abs(dot(z, vec2(0.0)));
    	float f = step(d, 1.0);
        fractalCol -= f;

        fractalCol = mix(fractalCol, vec3(0.1, 0.1, 0.2), 0.83);
    }

	fragColor = vec4(fractalCol * vec3(float(count) * scale), 1.0);
    fragColor.rgb = vec3(pow(fragColor.rgb, vec3(1.0 / 2.2)));
}
