#define t .005 // thickness as % of screen

float clampSinTime(float x)
{
    return (sin(iTime + x) + 1.) / 2.;
}

float drawLine(float line, float limit)
{
    return step(line, limit + t) * step(limit - t, line);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float horzMov = clampSinTime(0.) * .1 + .4;
    float vertSinCurve = uv.x - clampSinTime(uv.y * 7.5) * .1;

    float l = drawLine(horzMov, vertSinCurve);

    // Output to screen
    fragColor = vec4(l);
}
