const vec2 z = vec2(11.,7.);
const float complexity =11.;
const float density = .2; // 0-1

const float PI = atan(1.)*4.;

vec4 hash42(vec2 p)
{
    p+=1e4;
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

#define q(x,p) (floor((x)/(p))*(p))

void mainImage( out vec4 o, vec2 C)
{
    vec2 R = iResolution.xy;
    vec2 uv = C/R.xy;
    vec2 N = uv;
    float t = iTime*.05;
    t+=1e2;
    uv.x *= R.x/R.y;
    uv *= z;
    uv.x+=iTime;
    o = vec4(1);
    float s = 1.;
    float f = .95;

    for (float i = 1.;i <= complexity; ++ i) {
        vec2 c = floor(uv+i);
        vec4 h = hash42(c);
        vec2 p = fract(uv+i)-q(t*s,.2);
        uv+= s*p * h.z;
        c = floor(uv+i);
        s = -s*1.2;
        if (h.w < density) {
            o *= h;
        }
    }
    //o=fract(o);
    o=(step(.5,o) * mod(C.y,3.)/2.);
}
