void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 cam = texture(iChannel0, uv);
    vec4 self = texture(iChannel1, uv);

    fragColor = mix(cam, self, step(1.0, float(iFrame)) * 0.8);

