void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(0.6, 0.5 * cos(50.0 * iTime * uv.y) + 0.5, 0.8);

    vec3 a = color * smoothstep(0.42, 0.42, uv.x);
    vec3 b = color * (1.0 - smoothstep(0.58, 0.58, uv.x));
    vec3 f = clamp(a * b, 0.0, 1.0) * (1.0 - smoothstep(0.7, 0.7,uv.y)) * smoothstep(0.1, 0.1, uv.y);

    vec3 c = color * (1.0 - smoothstep(0.9, 0.9, uv.y));
    vec3 d = color * (smoothstep(0.7, 0.7, uv.y));
    vec3 e = clamp(c * d, 0.0, 1.0) * smoothstep(0.2, 0.2, uv.x) * (1.0 - smoothstep(0.8, 0.8, uv.x));

    fragColor = vec4(e + f,1.0);
}
