// variant of https://shadertoy.com/view/3lScRR
// variant of https://shadertoy.com/view/ttlcDs


void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy*.1, V = u/R.y, d, C,T=R-R,
    U = V*5. + V.y* cos( iTime + V.x*.56 + 11.*O.xw );
    for (int k=0; k<49; k++)
        C = U - round((U+vec2(k%7-3,k/7-3))*.2) / .2 ,
        V = C * inverse(transpose(mat2(dFdx(U),dFdy(U)) ))/R.y,
        d = max(V-V,1.-.05*dot(C,C)), d*=d*d*d,
        O += sin( 36.*length(V) -2.*iTime +(U-.2*C).x ) * d.x ,
        T += d*d;
    O = ( .5 + .1*O/sqrt(T.x) )* smoothstep(1.,-1.,U.y -37.);
}
