void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv.y += iTime*0.25;uv.x += sin(iTime)*0.3;
    vec3 col = vec3(4.*sin(8.*uv.y),
                    4.*cos(8.*uv.y),
                    4.*(1.-sin(8.*-uv.y + 0.1*uv.x)));
    float off = cos(uv.y*18. *iTime*0.05 + iTimeDelta*8.);
    col *= sin((uv.x-off*1e-2)*50.);
	col *= (uv.x -off*10.> 0. && uv.x -off*10. < 1. ? mat3(1,0,0,0,1,0,0,0,1):mat3(1,0,1,0,1,1,1,1,0));
    fragColor = vec4(col,1.0);
}
